        public MockLogBookRepository()
        {
            for (int i = 0; i < _data.Count; i++)
            {
                _data[i].ID = i + 1;
                _data[i].CustomerName = "Test Customer";
                _data[i].DepotName = "Cape Town";
                _data[i].RouteName = _data[i].DriverName == "Jabu" ? "205_1" : "CTC712_0";
            }
        }

        void ILogBookRepository.Add(LogBook value)
        {
            _data.Add(value);
        }

        void ILogBookRepository.Delete(int id)
        {
            var log = get(id);
            _data.Remove(log);
        }

        IEnumerable<LogBook> ILogBookRepository.Get()
        {
            return _data;
        }

        LogBook ILogBookRepository.Get(int id)
        {
            return get(id);
        }

        void ILogBookRepository.Update(int id, LogBook value)
        {
            var log = get(id);
            log.CustomerName = value.CustomerName;
            log.DepotName = value.DepotName;
            log.DriverName = value.DriverName;
            log.DriverReference = value.DriverReference;
            log.Lat = value.Lat;
            log.Long = value.Long;
            log.LocationName = value.LocationName;
            log.RouteName = value.RouteName;
            log.SequenceNo = value.SequenceNo;
        }

        private LogBook get(int id)
        {
            return _data.SingleOrDefault(l => l.ID == id);
        }

        List<Point> ILogBookRepository.GetPoints()
        {
            return _data.Select(d => new Point
            {
                Latitude = d.Lat,
                Longitude = d.Long
            }).ToList();
        }
    }
